import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Chilie from '../red1.png';
import User from '../location.png';


function ProductDetails() {
   const { productId } = useParams();
   const [data, setData] = useState([]);

   useEffect(() => {
      const fetchData = async () => {
         const result = await axios(
            `https://fakestoreapi.com/products/${productId}`
         );

         setData(result.data);
         //console.log(result);
      };

      fetchData();
   }, []);

   return (
      <>
         <section className='text-gray-700 body-font overflow-hidden bg-white'>
            <div className='container px-96 py-24 mx-auto'>
               <div className=''>
                  <img
                     src={Chilie}
                     alt='ecommerce'
                     className='w-96 p-4 object-cover object-center rounded border border-gray-200'
                  />
                      <div className='flex mt-2 items-center justify-between'>
                        <span className='text-x font-bold text-green-500 dark:text-white'>
                        Verified by owner
                        </span>
                        <span className='text-x font-bold text-green-500 dark:text-white'>
                        <img
                        className='w-3 h-5'
                        src={Location}
                        alt='product image'
                     />
                        </span>
                        <span className='text-x font-bold text-gray-900 dark:text-white'>
                        </span>               
                     </div>
               </div>
            </div>
         </section>
      </>
   );
}

export default ProductDetails;
