   const { productId } = useParams();
   console.log(typeof productId);
   const [data, setData] = useState([]);

   useEffect(() => {
      const fetchData = async () => {
         const result = await axios(
            `https://fakestoreapi.com/products/${productId}`
         );

         setData(result.data);
         console.log(result);
      };

      fetchData();
   }, []);

